(function($){$("#current-year").text((new Date).getFullYear()),$("html").removeClass("no-js"),
    $("header a").click(function(e){var href=$(this).attr("href");
    if(href.startsWith("#")){e.preventDefault();var t=$(href).offset().top;
    $("html,body").animate({scrollTop:t+"px"},Math.abs(window.pageYOffset-$(href).offset().top));
    $("header").hasClass("active")&&$("header,body").removeClass("active")}}),
    $("#to-top").click(function(){$("html,body").animate({scrollTop:0},500)}),
    $("#lead-down span").click(function(){var e=$("#lead").next().offset().top;
    $("html,body").animate({scrollTop:e+"px"},500)}),
    $("#mobile-menu-open").click(function(){$("header,body").addClass("active")}),
    $("#mobile-menu-close").click(function(){$("header,body").removeClass("active")}),
    $("#view-more-projects").click(function(e){e.preventDefault(),
    $(this).fadeOut(300,function(){$("#more-projects").fadeIn(300)})})})(jQuery);
    